import React, { Component } from "react";
import { connect } from "react-redux";
import LinkButton from "../../actions/authActions";


class MyFiles extends Component {



    render() {
      const { files } = this.state;
      const { user } = this.props.auth;

      return (
        <div style={{ height: "75vh" }} className="container valign-wrapper">
          <div className="row">
            <div className="landing-copy col s12 center-align">

            <h4>Files you've uploaded</h4>


            <table className="App-table">
            <thead>
              <tr>
                  <th>File</th>
                  <th>Uploaded</th>
                  <th>Size</th>
                  <th></th>
              </tr>
            </thead>
            <tbody>
              {files.map((file, index) => {
                var d = new Date(file.uploadDate);
                return (
                  <tr key={index}>
                    <td><a href={`http://localhost:3001/api/files/${file.filename}`}>{file.filename}</a></td>
                    <td>{`${d.toLocaleDateString()} ${d.toLocaleTimeString()}`}</td>
                    <td>{(Math.round(file.length/100) / 10)+'KB'}</td>
                    <td><button onClick={this.deleteFile.bind(this)} id={file._id}>Remove</button></td>
                  </tr>
                )
              })}
            </tbody>
          </table>









            </div>
          </div>
        </div>
      );
    }



  }



  const mapStateToProps = state => ({
    auth: state.auth
  });

  export default connect(
    mapStateToProps,
    {}
  )(MyFiles);
